function testPassword(passwd,idOfResult,idOfSubmit){var intScore=0;var strVerdict="weak";var strLog="";if(passwd.length>8){intScore=intScore+(passwd.length-8)*2}if(passwd.match(/[a-z]/)){intScore=intScore+1;strLog=strLog+"1 point for at least one lower case char\n"}if(passwd.match(/[A-Z]/)){intScore=intScore+5;strLog=strLog+"5 points for at least one upper case char\n"}if(passwd.match(/\d+/)){intScore=intScore+5;strLog=strLog+"5 points for at least one number\n"}if(passwd.match(/(.*[0-9].*[0-9].*[0-9])/)){intScore=intScore+5;strLog=strLog+"5 points for at least three numbers\n"}if(passwd.match(/.[!,@,#,$,%,^,&,*,?,_,~]/)){intScore=intScore+5;strLog=strLog+"5 points for at least one special char\n"}if(passwd.match(/(.*[!,@,#,$,%,^,&,*,?,_,~].*[!,@,#,$,%,^,&,*,?,_,~])/)){intScore=intScore+5;strLog=strLog+"5 points for at least two special chars\n"}if(passwd.match(/([a-z].*[A-Z])|([A-Z].*[a-z])/)){intScore=intScore+2;strLog=strLog+"2 combo points for upper and lower letters\n"}if(passwd.match(/([a-zA-Z])/)&&passwd.match(/([0-9])/)){intScore=intScore+2;strLog=strLog+"2 combo points for letters and numbers\n"}if(passwd.match(/([a-zA-Z0-9].*[!,@,#,$,%,^,&,*,?,_,~])|([!,@,#,$,%,^,&,*,?,_,~].*[a-zA-Z0-9])/)){intScore=intScore+2;strLog=strLog+"2 combo points for letters, numbers and special chars\n"}if(intScore<10){strVerdict="Das ist nicht dein Ernst."}else if(intScore>=10&&intScore<20){strVerdict='Na komm, das kannst du besser. Du brauchst nicht unbedingt Sonderzeichen zu verwenden; <a href="http://xkcd.com/936/" target="_blank">ausreichende Länge</a> tut\'s auch!'}else if(intScore>=20&&intScore<30){strVerdict="Na gut. Zur Not."}else if(intScore>=30&&intScore<45){strVerdict="Sehr vernünftig."}else{strVerdict="Respekt. Aber nicht auf ein Post-It schreiben."}if(intScore<=10){if(document.getElementById(idOfSubmit)!==null){document.getElementById(idOfSubmit).disabled=true}}else{if(document.getElementById(idOfSubmit)!==null){document.getElementById(idOfSubmit).disabled=false}}document.getElementById(idOfResult).innerHTML="Stärke "+intScore+" - "+strVerdict;return intScore}